#root {
  max-width: 1280px;
  margin: 0 auto;
  padding: 2rem;
  text-align: center;
  
}

.logo {
  height: 6em;
  padding: 1.5em;
  will-change: filter;
  transition: filter 300ms;
}
.logo:hover {
  filter: drop-shadow(0 0 2em #646cffaa);
}
.logo.react:hover {
  filter: drop-shadow(0 0 2em #61dafbaa);
}



.card {
  padding: 2em;
}

.read-the-docs {
  color: #888;
}

.App {
  min-height:100vh; 
	display:flex; 
	flex-direction:column; 
}
.todo-line{
  position: relative;
  max-width: 80%;
  margin: 2rem auto;
  padding: 1rem;
  border: 1px solid #666;
  box-shadow: 5px 5px 10px -5px;
  transition: 0.4s;
}
.todo-line:hover {
  cursor: pointer;
  box-shadow: 5px 5px 10px 0px;
}

.condition {
  position: relative;

}
.correct {

  position: absolute;
  top: 50%;
  left: 60%;
  transform: translate(-50%, -50%);
  height: 1em;
  will-change: filter;
  transition: filter 300ms;
}
